import axios from "axios";

// Первый экземпляр для вашего локального бэкенда
const instanceLocal = axios.create({
  baseURL: 'http://localhost:4444', // Локальный сервер
});

// Второй экземпляр для MockAPI
const instanceMockAPI = axios.create({
  baseURL: 'https://673876654eb22e24fca800c5.mockapi.io', // MockAPI
});

// Добавляем интерсептор для добавления токена в запросы
instanceLocal.interceptors.request.use((config) => {
  config.headers.Authorization = window.localStorage.getItem('token');
  return config;
});

instanceMockAPI.interceptors.request.use((config) => {
  config.headers.Authorization = window.localStorage.getItem('token');
  return config;
});

export { instanceLocal, instanceMockAPI };
