import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchAuthMe } from '../../redux/slices/auth';
import { fetchUserOrders } from '../../redux/slices/orders'; // Импортируем экшен для получения заказов
import Header from '../../components/header/Header';
import Footer from '../../components/footer/Footer';
import './profile.css';

const Profile = () => {
  const dispatch = useDispatch();

  // Получаем информацию о пользователе из Redux
  const userData = useSelector((state) => state.auth.data);
  const userOrders = useSelector((state) => state.orders.data); // Получаем заказы пользователя
  const status = useSelector((state) => state.auth.status); // Статус данных пользователя
  const ordersStatus = useSelector((state) => state.orders.status); // Статус заказов

  // При монтировании компонента, отправляем запросы для получения данных пользователя и заказов
  useEffect(() => {
    if (status === 'loading') {
      dispatch(fetchAuthMe());  // Получаем данные пользователя
    }
    if (ordersStatus === 'loading') {
      dispatch(fetchUserOrders()); // Получаем данные заказов
    }
  }, [dispatch, status, ordersStatus]);

  // Если данные загружаются, показываем сообщение о загрузке
  if (status === 'loading' || ordersStatus === 'loading') {
    return <p>Загрузка...</p>;
  }

  // Если возникла ошибка или данные не найдены
  if (status === 'error' || !userData) {
    return <p>Данные пользователя не найдены</p>;
  }

  if (ordersStatus === 'error' || !userOrders) {
    return <p>Ошибка загрузки заказов</p>;
  }

  return (
    <>
      <Header />
      <div className="profile">
        <div className="container">
          <div className="profile-card">
            <div className="profile-header">
              <img 
                src={userData.avatarUrl || "img/icons/logo.svg"} 
                alt="User Avatar"
                className="profile-avatar"
              />
              <h1 className="profile-name">{userData.fullName || "Имя пользователя"}</h1>
              <p className="profile-email">{userData.email || "example@mail.com"}</p>
            </div>
            <div className="profile-actions">
              <button className="btn edit-btn">Редактировать профиль</button>
              <button className="btn logout-btn">Выйти из аккаунта</button>
            </div>
            {/* Раздел заказов */}
            <div className="profile-orders">
              <h2 className="orders-title">Ваши заказы</h2>
              {userOrders && userOrders.length > 0 ? (
                <ul className="orders-list">
                  {userOrders.map((order) => (
                    <li key={order.id} className="order-item">
                      <span className="order-date">{order.date}</span>
                      <span className="order-status">{order.status}</span>
                      <span className="order-total">Сумма: {order.total} ₽</span>
                    </li>
                  ))}
                </ul>
              ) : (
                <p>У вас нет заказов</p>
              )}
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </>
  );
};

export default Profile;
