import React, {useState, createContext} from "react";
import axios from "axios";
import { useEffect, useCallback  } from 'react';
export const CartContext = createContext();

const Context = (props) => {

  const [items, setitems] = React.useState([]);
    const [cartItems, setCartItems] = React.useState([]);
    
    // Преобразуем price в число
    const processedItems = cartItems.map((item) => ({
      ...item,
      price: Number(item.price), // Убедимся, что price числовой
  }));


  const onAddToCart = useCallback(async (obj) => {
    try {
        console.log("Sending item to server:", obj);
        const { data } = await axios.post(
            'https://673876654eb22e24fca800c5.mockapi.io/cart',
            obj
        );
        console.log("Response from server:", data);
        setCartItems((prev) => {
            if (prev.some((item) => item.id === data.id)) {
                console.warn("Duplicate item:", data);
                return prev;
            }
            return [...prev, data];
        });
    } catch (error) {
        console.error("Ошибка при добавлении товара:", error);
    }
}, []);




  // Загрузка данных корзины при монтировании компонента
  useEffect(() => {
      const fetchData = async () => {
          try {
              const response = await axios.get('https://673876654eb22e24fca800c5.mockapi.io/cart');
              setCartItems(response.data);
          } catch (error) {
              console.error("Ошибка при загрузке данных:", error);
          }
      };

      fetchData();
  }, []); // Выполняется только при монтировании компонента
  
    const onRemoveItem = (id) => {
      
      axios.delete(`https://673876654eb22e24fca800c5.mockapi.io/cart/${id}`);
      setCartItems((prev) => prev.filter(item => item.id !== id));
    }

    const [activeCategory, setActiveCategory] = useState(''); // Состояние для активной категории

  const value = {
    items,
    processedItems,
    setitems,
    cartItems,
    activeCategory,
    setActiveCategory,
    setCartItems,
    onAddToCart,
    onRemoveItem,
  }
    return (
      <CartContext.Provider value={value}>
        {props.children}
      </CartContext.Provider>
        
        
    );
}
export default Context;
