import React, { useContext } from "react";
import { CartContext } from "../../components/Context";
import { Link } from 'react-router-dom';
import toCart from './../../img/pictures/toCart.svg';
import inCart from './../../img/pictures/inCart.svg';

function Card({ id, imageUrl, category, title, price }) {
    const { onAddToCart, cartItems } = useContext(CartContext);

    // Проверяем, добавлен ли товар в корзину
    const isAdded = cartItems.some((cartItem) => cartItem.id === id);

    const handleAddToCart = () => {
        const productToAdd = { id, imageUrl, category, title, price };
        onAddToCart(productToAdd); // Добавляем товар в корзину
    };

    return (
        <div className="card" key={id}>
            <Link to={`/tovar/${encodeURIComponent(title)}`}>
                <button className="img">
                    <img src={imageUrl} alt="product" />
                </button>
            </Link>
            <p>{category}</p>
            <a>{title}</a>
            <b>{price} ₽.</b>
            <button className="add">
                {!isAdded ? (
                    <button onClick={handleAddToCart}>
                        <img className="img2" src={toCart} alt="Add to Cart" />
                    </button>
                ) : (
                    <Link to="/cart">
                        <img className="img2" src={inCart} alt="In Cart" />
                    </Link>
                )}
            </button>
        </div>
    );
}

export default Card;
