import toCart from './../../img/pictures/toCart.svg'
import cartIcon from './../../img/icons/cart.png'
import inCart from './../../img/pictures/inCart.svg'

import { Link } from 'react-router-dom';
import React, { useContext, useState, useEffect } from "react";
import { CartContext } from '../../components/Context';

function Card({ id, imageUrl, category, title, price }) {
     // Получаем состояние корзины и функцию добавления товара из контекста
     const { cartItems, onAddToCart } = useContext(CartContext);

     // Проверяем, есть ли товар в корзине
     const isAdded = cartItems.some((cartItem) => cartItem.id === id);
 
     // Обработчик добавления в корзину
     const onAdd = () => {
         if (!isAdded) {
             // Передаем в onAddToCart все необходимые данные, включая правильный id
             onAddToCart({ id, imageUrl, category, title, price });
         }
     };

    return (
        <div className="card" key={id}> {/* Используем id */}
            <button className="img">
            <Link to={`/tovar/${encodeURIComponent(title)}`}>
            <img src={imageUrl} alt="products" />
            </Link>
            </button>
            <p>{category}</p>
            <a>{title}</a>
            <b>{price} ₽.</b>
            <button className="add">
                {!isAdded ? (
                    <button className="" onClick={onAdd}>
                        <img className="img2" src={toCart} alt="Add to Cart" />
                    </button>
                ) : (
                    <Link to="/cart">
                        <img className="img2" src={inCart} alt="In Cart" />
                    </Link>
                )}
            </button>
        </div>
    );
}

export default Card;
