import React from 'react';
import { Link } from 'react-router-dom';
import toCart from './../../img/pictures/toCart.svg';
import inCart from './../../img/pictures/inCart.svg';
import './card.css';

function Card({ id, imageUrl, category, title, price, onClickAdd, isInCart }) {
    // Используем состояние корзины, если не передаётся глобально
    const [isAdded, setIsAdded] = React.useState(isInCart || false);

    const onAdd = () => {
        onClickAdd({ id, imageUrl, category, title, price });
        setIsAdded(true); // Устанавливаем состояние как "добавлено"
    };

    return (
        <div className="card">
            {/* Картинка товара с переходом */}
            <button className="card-image">
                <Link to="/tovar">
                    <img src={imageUrl} alt={title} />
                </Link>
            </button>

            {/* Информация о продукте */}
            <p className="card-category">{category}</p>
            <h3 className="card-title">{title}</h3>
            <b className="card-price">{price} ₽</b>

            {/* Кнопка добавления в корзину */}
            <button className="card-add" onClick={onAdd} disabled={isAdded}>
                {!isAdded ? (
                    <img className="card-icon" src={toCart} alt="Add to Cart" />
                ) : (
                    <Link to="/cart">
                        <img className="card-icon" src={inCart} alt="In Cart" />
                    </Link>
                )}
            </button>
        </div>
    );
}

export default Card;
