import React, { useContext, useState } from 'react';
import './admin.css';
import Header from './../../components/header/Header';
import Footer from '../../components/footer/Footer';
import { CartContext } from '../../components/Context';
import { useForm } from 'react-hook-form';

const Admin = () => {
  const { items, addProduct, updateProduct, deleteProduct } = useContext(CartContext);

  const {
    register,
    handleSubmit,
    formState: { errors, isValid },
    reset,
  } = useForm({
    defaultValues: {
      category: '',
      title: '',
      price: 0,
      imageUrl: '',
    },
    mode: 'onChange',
  });

  const [deleteId, setDeleteId] = useState('');
  const [deleteError, setDeleteError] = useState('');
  const [showErrors, setShowErrors] = useState(false);

  const handleAdd = async (data) => {
    try {
      const productWithId = { ...data, id: Date.now() };
      await addProduct(productWithId);
      reset(); // Сброс формы после успешного добавления
      setShowErrors(false); // Сброс отображения ошибок
    } catch (e) {
      console.error(e);
    }
  };

  const handleDelete = async () => {
    if (!deleteId) {
      setDeleteError('Введите ID продукта для удаления.');
      return;
    }

    try {
      await deleteProduct(deleteId);
      setDeleteError(''); // Сброс ошибок после успешного удаления
    } catch (e) {
      setDeleteError('Ошибка при удалении продукта. Проверьте ID.');
    }
  };

  return (
    <>
      <Header />
      <section className="admin">
        <div className="container">
          <h1 className="prei">Админ-панель</h1>

          {/* Добавление товара */}
          <div className="crud-section">
            <h2>Добавить продукт</h2>
            <form onSubmit={handleSubmit(handleAdd)}>
              <div className="form-group">
                <input
                  type="text"
                  placeholder="Название"
                  {...register('title', { required: 'Введите название продукта.' })}
                  className="form-control-item"
                />
                {showErrors && errors.title && <p className="check">{errors.title.message}</p>}
              </div>
              <div className="form-group">
                <input
                  type="text"
                  placeholder="Категория"
                  {...register('category', { required: 'Введите категорию продукта.' })}
                  className="form-control-item"
                />
                {showErrors && errors.category && <p className="check">{errors.category.message}</p>}
              </div>
              <div className="form-group">
                <input
                  type="number"
                  placeholder="Цена"
                  {...register('price', {
                    required: 'Введите цену продукта.',
                    min: { value: 1, message: 'Цена должна быть больше 0.' },
                  })}
                  className="form-control-item"
                />
                {showErrors && errors.price && <p className="check">{errors.price.message}</p>}
              </div>
              <div className="form-group">
                <input
                  type="text"
                  placeholder="URL изображения"
                  {...register('imageUrl', { required: 'Введите URL изображения продукта.' })}
                  className="form-control-item"
                />
                {showErrors && errors.imageUrl && <p className="check">{errors.imageUrl.message}</p>}
              </div>
              <button
                type="submit"
                className="CRUD-btn"
                onClick={() => setShowErrors(true)} // Показ ошибок после клика
              >
                Добавить продукт
              </button>
            </form>
          </div>

          {/* Удаление товара */}
          <div className="crud-section">
            <h2>Удалить продукт</h2>
            <input
              type="number"
              placeholder="ID продукта для удаления"
              value={deleteId}
              onChange={(e) => setDeleteId(e.target.value)}
              className="form-control-item"
            />
            {deleteError && <p className="check">{deleteError}</p>}
            <button className="CRUD-btn" onClick={handleDelete}>
              Удалить продукт
            </button>
          </div>

          {/* Список товаров */}
          <div className="crud-section">
            <h2>Список товаров</h2>
            {items.length > 0 ? (
              <table className="product-table">
                <thead>
                  <tr className="shapka">
                    <th>ID</th>
                    <th>Название</th>
                    <th>Категория</th>
                    <th>Цена</th>
                    <th>Изображение</th>
                  </tr>
                </thead>
                <tbody>
                  {items.map((item) => (
                    <tr key={item.id}>
                      <td>{item.id}</td>
                      <td>{item.title}</td>
                      <td>{item.category}</td>
                      <td>{item.price} руб.</td>
                      <td>
                        <img
                          src={item.imageUrl}
                          alt={item.title}
                          width="50"
                          height="50"
                        />
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            ) : (
              <p>Список товаров пуст</p>
            )}
          </div>
        </div>
      </section>
      <Footer />
    </>
  );
};

export default Admin;
