import React from "react";
import { Link } from "react-router-dom";
import toCart from "./../../img/pictures/toCart.svg";
import inCart from "./../../img/pictures/inCart.svg";

function Card({ id, imageUrl, category, title, price, onClickAdd, isAdded }) {
    const onAdd = () => {
        onClickAdd({ id, imageUrl, category, title, price });
    };
    

    return (
        <div className="card" key={id}>
            <Link to={`/tovar/${encodeURIComponent(title)}`}>
                <button className="img">
                    <img src={imageUrl} alt="products" />
                </button>
            </Link>
            <p>{category}</p>
            <a>{title}</a>
            <b>{price} â‚½.</b>
            <button className="add" onClick={!isAdded ? onAdd : null}>
                {!isAdded ? (
                    <img className="img2" src={toCart} alt="Add to Cart" />
                ) : (
                    <Link to="/cart">
                        <img className="img2" src={inCart} alt="In Cart" />
                    </Link>
                )}
            </button>
        </div>
    );
}

export default Card;
