import React, { useContext, useState } from 'react';
import { CartContext } from '../Context';
import { Link } from 'react-router-dom';

const CartItem = ({ onRemove }) => {
    const { cartItems } = useContext(CartContext);

  const{ processedItems } = useContext(CartContext);

  // Хук useState для управления значением счетчика
  const [count, setCount] = useState(1);

  // Функция для уменьшения счетчика
  const decrement = () => {
    if (count > 0) setCount(count - 1);
  };

  // Функция для увеличения счетчика
  const increment = () => {
    setCount(count + 1);
  };
    // Общая сумма товаров
    const totalItems = processedItems.length;
    const totalPrice = processedItems.reduce((sum, item) => sum + item.price, 0);

    return (
        <>
            <div className="container">
                <div className="cart">
                    {processedItems.length > 0 ? (
                        <div className="cart-main">
                            <div className="precart">
                                <span>КОРЗИНА</span>
                                <a onClick={() => onRemove('all')}>Очистить всю корзину</a>
                            </div>
                            <div className="items">
                                {processedItems.map((obj) => (
                                    <div className="cartItem" key={obj.id}>
                                        <div className="cartItemImg">
                                            <img
                                                src={obj.imageUrl}
                                                alt={obj.title}
                                                width={50}
                                                height={50}
                                            />
                                        </div>
                                        <div className="main">
                                            <div className="descrip">
                                                <div className="category">{obj.category}</div>
                                                <div className="name">{obj.title}</div>
                                            </div>

                                            <div className="number">
      <button className="minus" onClick={decrement}>-</button>
      <span className="counter">{count}</span>
      <button className="plus" onClick={increment}>+</button>
    </div>

                                            <div className="price">
                                                {obj.price.toLocaleString('ru-RU')} руб.
                                            </div>

                                            <button
                                                onClick={() => onRemove(obj.id)}
                                                className="trashImg"
                                            >
                                                <img src="img/products/TrashCan.svg" alt="Remove" />
                                            </button>
                                        </div>
                                    </div>
                                ))}
                            </div>

                            <div className="order-block">
                                <div className="total-menu">
                                    <div className="main-total">
                                        <div className="subtitle">
                                            <span>ВАШ ЗАКАЗ</span>
                                        </div>
                                        <div className="basket-row-total">
                                            <a>{totalItems} товаров</a>
                                            <a>{totalPrice.toLocaleString('ru-RU')} р.</a>
                                        </div>
                                        <div className="basket-row-final">
                                            <b>Итого</b>
                                            <b>{totalPrice.toLocaleString('ru-RU')} р.</b>
                                        </div>

                                        <button className="buy">Купить</button>
                                    </div>
                                </div>

                                <div className="aftertitle">
                                    <span>Только самовывоз!</span>
                                </div>
                            </div>
                        </div>
                    ) : (
                        <div className="cartEmpty">
                            <div className="precart2">
                                <span>КОРЗИНА</span>
                            </div>
                            <div className="center">
                                <h1>В корзине пока нет товаров</h1>
                                <p>Добавляйте понравившиеся товары и наслаждайтесь покупками️</p>
                                <Link to="/catalog">
                                    <button className="toCatalog">
                                        <img src="img/pic/toCatalog.svg" alt="To Catalog" />
                                    </button>
                                </Link>
                            </div>
                        </div>
                    )}
                </div>
            </div>
        </>
    );
};

export default CartItem;
