import React, { useContext } from "react";
import { Link } from 'react-router-dom';
import { CartContext } from '../../components/Context'; // Импортируем контекст
import toCart from './../../img/pictures/toCart.svg';
import inCart from './../../img/pictures/inCart.svg';

function Card({ id, imageUrl, category, title, price }) {
    // Получаем состояние корзины и функцию добавления товара из контекста
    const { cartItems, onAddToCart } = useContext(CartContext);

    // Проверяем, есть ли товар в корзине
    const isAdded = cartItems.some((cartItem) => cartItem.id === id);

    // Преобразуем price в число и форматируем
    const numericPrice = Number(price); // Убедитесь, что price является числом
    const formattedPrice = numericPrice.toLocaleString('ru-RU'); // Добавляет пробелы в разряды

    // Обработчик добавления в корзину
    const onAdd = () => {
        if (!isAdded) {
            onAddToCart({ id, imageUrl, category, title, price: numericPrice }); // Передаем число
        }
    };

    return (
        <div className="card" key={title}> {/* Используем id */}
        <Link to={`/tovar/${encodeURIComponent(title)}`}>
            <button className="img">
                <img src={imageUrl} alt="products" />
            </button>
        </Link>
            <p>{category}</p>
            <a>{title}</a>
            <b>{formattedPrice} ₽.</b> {/* Отображаем форматированную цену */}
            <button className="add">
                {!isAdded ? (
                    <button className="" onClick={onAdd}>
                        <img className="img2" src={toCart} alt="Add to Cart" />
                    </button>
                ) : (
                    <Link to="/cart">
                        <img className="img2" src={inCart} alt="In Cart" />
                    </Link>
                )}
            </button>
        </div>
    );
}

export default Card;
