import React from "react";
import "./registration.css";
import { Link } from "react-router-dom";
import Header from "../../components/header/Header";
import { fetchRegister, selectIsAuth } from "../../redux/slices/auth";
import { useDispatch, useSelector } from "react-redux";
import { Navigate } from "react-router-dom";
import { useForm } from "react-hook-form";

export const Registration = () => {
  const isAuth = useSelector(selectIsAuth);
  const dispatch = useDispatch();
  const {
    register,
    handleSubmit,
    formState: { errors, isValid },
  } = useForm({
    defaultValues: {
      fullName: "",
      email: "",
      password: "",
    },
    mode: "onChange",
  });

  const onSubmit = async (values) => {
    const data = await dispatch(fetchRegister(values));
    if (!data.payload) {
      return alert("Не удалось зарегистрироваться");
    }
    if ("token" in data.payload) {
      window.localStorage.setItem("token", data.payload.token);
    }
  };

  if (isAuth) {
    return <Navigate to="/" />;
  }

  return (
    <>
      <Header />
      <section className="Login">
        <div className="preview">
          <h3 className="text-center">Регистрация</h3>
          <div className="registration-cssave">
            <form onSubmit={handleSubmit(onSubmit)}>
              <div className="form-group">
                <input
                  className="form-control-item"
                  type="text"
                  placeholder="Логин"
                  {...register("fullName", {
                    required: "Введите логин",
                    minLength: { value: 4, message: "Минимум 4 символа" },
                    maxLength: { value: 15, message: "Максимум 15 символов" },
                  })}
                />
                {errors.fullName && <p>{errors.fullName.message}</p>}
              </div>
              <div className="form-group">
                <input
                  className="form-control-item"
                  type="email"
                  placeholder="Email"
                  {...register("email", { required: "Введите почту" })}
                />
                {errors.email && <p>{errors.email.message}</p>}
              </div>
              <div className="form-group">
                <input
                  className="form-control-item"
                  type="password"
                  placeholder="Пароль"
                  {...register("password", {
                    required: "Введите пароль",
                    minLength: { value: 6, message: "Минимум 6 символов" },
                  })}
                />
                {errors.password && <p>{errors.password.message}</p>}
              </div>
              <div className="form-group">
                <button
                  disabled={!isValid}
                  className="btn btn-primary btn-block create-account"
                  type="submit"
                >
                  СОЗДАТЬ
                </button>
              </div>
              <div className="form-group">
                <Link to="/login">
                  <button className="log-in" type="button">
                    Есть аккаунт? Войти
                  </button>
                </Link>
              </div>
            </form>
          </div>
        </div>
      </section>
    </>
  );
};

export default Registration;
