import React, { useState, useEffect, useContext } from 'react';
import Card from '../card/Card';
import { CartContext } from '../Context';

import axios from 'axios';

const Categories = () => {
    const [categories] = useState([
        { key: 'Акустические гитары', name: 'Акустические гитары' },
        { key: 'Электрогитары', name: 'Электрогитары' },
        { key: 'Классические гитары', name: 'Классические гитары' },
        { key: 'Усилители для гитар', name: 'Усилители для гитар' },
        { key: 'Цифровое пианино', name: 'Цифровое пианино' },
        { key: 'Барабаны', name: 'Барабаны' },
    ]);
    const [activeCategory, setActiveCategory] = useState('');
    const [items, setItems] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const { cartItems, onAddToCart, setCartItems } = useContext(CartContext);

    useEffect(() => {
        axios
            .get('https://673876654eb22e24fca800c5.mockapi.io/items')
            .then((response) => {
                setItems(response.data);
                setLoading(false);
            })
            .catch((error) => {
                setError(error.message);
                setLoading(false);
            });

        axios
            .get('https://673876654eb22e24fca800c5.mockapi.io/cart')
            .then((response) => setCartItems(response.data))
            .catch((error) => console.error('Failed to load cart items:', error));
    }, [setCartItems]);

    const filteredItems = activeCategory
        ? items.filter((item) => item.category === activeCategory)
        : items;

    return (
        <div className="container">
            <div className="categories">
                <span>Категории</span>
                <div className="classes">
                    {categories.map((category) => (
                        <button
                            key={category.key}
                            onClick={() => setActiveCategory(category.key)}
                            className={activeCategory === category.key ? 'active' : ''}
                        >
                            {category.name}
                        </button>
                    ))}
                </div>
            </div>

            {loading && <p>Загрузка данных...</p>}
            {error && <p style={{ color: 'red' }}>{error}</p>}

            <div className="cards">
                {!loading && filteredItems.length === 0 && (
                    <p>Нет элементов в этой категории.</p>
                )}
                {filteredItems.map((item) => (
                    <Card key={item.id} onClickAdd={(obj) => onAddToCart(obj)} {...item} />
                ))}
            </div>
        </div>
    );
};

export default Categories;
