import toCart from './../../img/pictures/toCart.svg';
import inCart from './../../img/pictures/inCart.svg';
import React, { useContext } from "react";
import { Link } from 'react-router-dom';
import { CartContext } from '../Context';

function Card({ id, imageUrl, category, title, price, onClickAdd }) {
    const { cartItems } = useContext(CartContext); // Получаем cartItems из контекста
    const isAdded = cartItems.some((item) => item.title === title); // Проверяем, есть ли товар в корзине



    return (
        <div className="card" key={id}> {/* Используем id */}
            <Link to={`/tovar/${encodeURIComponent(title)}`}>
                <button className="img">
                    <img src={imageUrl} alt="products" />
                </button>
            </Link>
            <p>{category}</p>
            <a>{title}</a>
            <b>{price} ₽.</b>
            <button className="add">
                {!isAdded ? (
                    <button className="" onClick={onAdd}>
                        <img className="img2" src={toCart} alt="Add to Cart" />
                    </button>
                ) : (
                    <Link to="/cart">
                        <img className="img2" src={inCart} alt="In Cart" />
                    </Link>
                )}
            </button>
        </div>
    );
}

export default Card;
