import React, { useState, createContext, useEffect, useCallback } from "react";
import axios from "axios";

export const CartContext = createContext();

const Context = (props) => {
    const [items, setItems] = React.useState([]); // Товары
    const [cartItems, setCartItems] = React.useState([]); // Корзина
    const [activeCategory, setActiveCategory] = React.useState(''); // Активная категория

    // Загрузка товаров
    useEffect(() => {
        const fetchItems = async () => {
            try {
                const response = await axios.get('https://example.com/api/items');
                setItems(response.data);
            } catch (error) {
                console.error("Ошибка при загрузке товаров:", error);
            }
        };
        fetchItems();
    }, []);

    // Загрузка корзины из localStorage при монтировании компонента
    useEffect(() => {
        const savedCartItems = JSON.parse(localStorage.getItem('cartItems'));
        if (savedCartItems) {
            setCartItems(savedCartItems); // Если есть данные в localStorage, загружаем их
        }
    }, []);

    // Сохранение корзины в localStorage при изменении состояния cartItems
    useEffect(() => {
        if (cartItems.length > 0) {
            localStorage.setItem('cartItems', JSON.stringify(cartItems));
        }
    }, [cartItems]);

    // Добавление товара в корзину
    const onAddToCart = useCallback((obj) => {
        setCartItems((prev) => [...prev, obj]); // Добавляем товар в корзину
    }, []);

    // Удаление товара из корзины
    const onRemoveItem = (id) => {
        setCartItems((prev) => prev.filter(item => item.id !== id)); // Удаляем товар из корзины
    };

    const value = {
        items,
        setItems,
        cartItems,
        activeCategory,
        setActiveCategory,
        setCartItems,
        onAddToCart,
        onRemoveItem,
    };

    return (
        <CartContext.Provider value={value}>
            {props.children}
        </CartContext.Provider>
    );
};

export default Context;
