import toCart from './../../img/pictures/toCart.svg'
import cartIcon from './../../img/icons/cart.png'
import inCart from './../../img/pictures/inCart.svg'

import { Link } from 'react-router-dom';
import React, { useContext } from 'react';
import { CartContext } from '../../components/Context';

function Card({ id, imageUrl, category, title, price, onClickAdd }) {
    const { items } = useContext(CartContext); // Получение данных из контекста

  
    const item = items.find(
      (item) => item.title.toLowerCase() === decodeURIComponent(title).toLowerCase()
    );
  
    const { onAddToCart, cartItems } = useContext(CartContext);
  
    if (!item) return <p>Товар не найден</p>; // Обработка ошибки, если товара нет
  
    const isAdded = cartItems.some((cartItem) => cartItem.id === item.id);

    return (
        <div className="card" key={id}> {/* Используем id */}
            <button className="img">
            <Link to={`/tovar/${encodeURIComponent(title)}`}>
            <img src={imageUrl} alt="products" />
            </Link>
            </button>
            <p>{category}</p>
            <a>{title}</a>
            <b>{price} ₽.</b>
            <button className="add">
            {!isAdded ? (
                        <button className="" onClick={() => {
                            const productToAdd = {
                                id: item.id,
                                title: item.title,
                                price: item.price,
                                category: item.category,
                                imageUrl: item.imageUrl,
                            };
                            onAddToCart(productToAdd); // Добавляем товар в корзину
                        }} disabled={isAdded}>
                            <img className="img2" src={toCart} alt="Add to Cart" />
                        </button>
                    ) : (
                        <Link to="/cart">
                            <img className="img2" src={inCart} alt="In Cart" />
                        </Link>
                    )}
            </button>
        </div>
    );
}

export default Card;
