import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchAuthMe, selectIsAuth } from '../../redux/slices/auth';
import './profile.css';
import Header from '../../components/header/Header';
import Footer from '../../components/footer/Footer';

const Profile = () => {
  const dispatch = useDispatch();
  const isAuth = useSelector(selectIsAuth);  // Проверяем, авторизован ли пользователь
  const user = useSelector((state) => state.auth.data);  // Получаем данные пользователя из Redux
  const status = useSelector((state) => state.auth.status);  // Статус загрузки данных

  useEffect(() => {
    if (isAuth) {
      dispatch(fetchAuthMe());  // Загружаем данные пользователя при авторизации
    }
  }, [dispatch, isAuth]);

  if (!isAuth) {
    return (
      <div>
        <p>Пожалуйста, авторизуйтесь, чтобы просмотреть профиль.</p>
      </div>
    );
  }

  if (status === 'loading') {
    return <div>Загрузка...</div>;  // Показываем индикатор загрузки
  }

  if (status === 'error') {
    return <div>Произошла ошибка при загрузке данных профиля.</div>;  // Показываем ошибку
  }

  return (
    <>
      <Header />
      <section className="profile">
        <div className="container">
          <div className="profile-card">
            <div className="profile-header">
              <img
                src={user?.avatar || "img/pic/avatar.jpg"} // Используем данные о пользователе или заглушку
                alt="User Avatar"
                className="profile-avatar"
              />
              <h1 className="profile-name">{user?.name || 'Имя пользователя'}</h1>
              <p className="profile-email">{user?.email || 'example@mail.com'}</p>
            </div>
            <div className="profile-actions">
              <button className="btn edit-btn">Редактировать профиль</button>
              <button className="btn logout-btn">Выйти из аккаунта</button>
            </div>
          </div>
        </div>
      </section>
      <Footer />
    </>
  );
};

export default Profile;
