import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchAuthMe } from '../../redux/slices/auth'; // Для получения данных пользователя
import { selectIsAuth } from '../../redux/slices/auth'; // Проверка авторизации

import './profile.css';

const Profile = () => {
  const dispatch = useDispatch();
  const isAuth = useSelector(selectIsAuth);  // Проверяем, авторизован ли пользователь
  const userData = useSelector((state) => state.auth.data);  // Получаем данные пользователя из Redux

  // При монтировании компонента, отправляем запрос для получения данных пользователя
  useEffect(() => {
    if (isAuth) {
      dispatch(fetchAuthMe());  // Получаем данные авторизованного пользователя
    }
  }, [isAuth, dispatch]);

  if (!isAuth) {
    return <p>Вы не авторизованы</p>;  // Если пользователь не авторизован, показываем это сообщение
  }

  return (
    <div className="profile">
      <div className="container">
        <div className="profile-card">
          <div className="profile-header">
            {/* Если есть аватар пользователя, отображаем его */}
            <img 
              src={userData.avatarUrl || "img/pic/avatar.jpg"} // Если аватар не задан, показываем дефолтный
              alt="User Avatar"
              className="profile-avatar"
            />
            <h1 className="profile-name">{userData.fullName || "Имя пользователя"}</h1>
            <p className="profile-email">{userData.email || "example@mail.com"}</p>
          </div>
          <div className="profile-actions">
            <button className="btn edit-btn">Редактировать профиль</button>
            <button className="btn logout-btn">Выйти из аккаунта</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
