import React, {useContext} from 'react'
import Card from '../card/Card'
import { CartContext } from '../Context'
import './product.css'
import axios from 'axios'

export class Categories extends Component {
    constructor(props) {
        super(props);
        this.state = {
            categories: [
                { key: 'Акустические гитары', name: 'Акустические гитары' },
                { key: 'Электрогитары', name: 'Электрогитары' },
                { key: 'Классические гитары', name: 'Классические гитары' },
                { key: 'Усилители для гитар', name: 'Усилители для гитар' },
                { key: 'Цифровое пианино', name: 'Цифровое пианино' },
                { key: 'Барабаны', name: 'Барабаны' },
            ],
            activeCategory: '', // Текущая выбранная категория
            items: [], // Список карточек (загружается с MockAPI)
            loading: true, // Индикатор загрузки данных
            error: null, // Ошибки при загрузке
        };
    }

    // Загрузка данных из MockAPI
    componentDidMount() {
        fetch('https://673876654eb22e24fca800c5.mockapi.io/items') // Укажите свой URL MockAPI
            .then((response) => {
                if (!response.ok) {
                    throw new Error('Failed to fetch data');
                }
                return response.json();
            })
            .then((data) => {
                this.setState({ items: data, loading: false });
            })
            .catch((error) => {
                this.setState({ error: error.message, loading: false });
            });
    }

    // Обработчик выбора категории
    handleCategoryClick = (categoryKey) => {
        this.setState({ activeCategory: categoryKey });
    };

    render() {
        
        const { categories, activeCategory, items, loading, error } = this.state;

        // Фильтрация карточек по выбранной категории
        const filteredItems = activeCategory
            ? items.filter((item) => item.category === activeCategory)
            : items;

        return (
            <>
            <div className="container">
                {/* Секция категорий */}
                <div className="categories">
                    <span>Категории</span>
                    <div className="classes">
                        {categories.map((category) => (
                            <button
                                key={category.key}
                                onClick={() => this.handleCategoryClick(category.key)}
                                className={activeCategory === category.key ? 'active' : ''}
                            >
                                {category.name}
                            </button>
                        ))}
                    </div>
                </div>

                {/* Индикатор загрузки или отображение ошибки */}
                {loading && <p>Загрузка данных...</p>}
                {error && <p style={{ color: 'red' }}>{error}</p>}

                {/* Секция карточек */}
                <div className="cards">
                    {!loading && filteredItems.length === 0 && (
                        <p>Нет элементов в этой категории.</p>
                    )}
                    {/* {filteredItems.map((item) => (
                        <Card />
                        // <div key={item.id} className="card">
                        //     <h3>{item.name}</h3>
                        //     <p>{item.category}</p>
                        // </div>
                    ))} */}

                { items.map((item) => ( 
                     <Card 
                    
                    key={item.title}
                    onClickAdd={(obj) => onAddToCart(obj)}
                    {...item}
                />
                 ))}
                </div>
            </div>
            </>
        );
    }
}

export default Categories;
