import React, { Component } from 'react';
import Card from '../card/Card';

import axios from 'axios';

export class Categories extends Component {
    constructor(props) {
        super(props);
        this.state = {
            categories: [
                { key: 'Акустические гитары', name: 'Акустические гитары' },
                { key: 'Электрогитары', name: 'Электрогитары' },
                { key: 'Классические гитары', name: 'Классические гитары' },
                { key: 'Усилители для гитар', name: 'Усилители для гитар' },
                { key: 'Цифровое пианино', name: 'Цифровое пианино' },
                { key: 'Барабаны', name: 'Барабаны' },
            ],
            activeCategory: '', // Текущая выбранная категория
            items: [], // Данные карточек
            loading: true, // Флаг загрузки
            error: null, // Флаг ошибки
        };
    }

    // Загрузка данных
    componentDidMount() {
        axios
            .get('https://673876654eb22e24fca800c5.mockapi.io/items') // Укажите ваш URL MockAPI
            .then((response) => {
                this.setState({ items: response.data, loading: false });
            })
            .catch((error) => {
                this.setState({ error: error.message, loading: false });
            });
    }

    // Обработчик выбора категории
    handleCategoryClick = (categoryKey) => {
        this.setState({ activeCategory: categoryKey });
    };

    render() {
        const { categories, activeCategory, items, loading, error } = this.state;

        // Фильтрация карточек по категории
        const filteredItems = activeCategory
            ? items.filter((item) => item.category === activeCategory)
            : items;

        return (
            <div className="container">
                {/* Блок категорий */}
                <div className="categories">
                    <span>Категории</span>
                    <div className="classes">
                        {categories.map((category) => (
                            <button
                                key={category.key}
                                onClick={() => this.handleCategoryClick(category.key)}
                                className={activeCategory === category.key ? 'active' : ''}
                            >
                                {category.name}
                            </button>
                        ))}
                    </div>
                </div>

                {/* Состояние загрузки/ошибки */}
                {loading && <p>Загрузка данных...</p>}
                {error && <p style={{ color: 'red' }}>{error}</p>}

                {/* Блок карточек */}
                <div className="cards">
                    {!loading && filteredItems.length === 0 && (
                        <p>Нет элементов в этой категории.</p>
                    )}
                    {filteredItems.map((item) => (
                        <Card
                            key={item.title} // Используем уникальное значение
                            title={item.title}
                            price={item.price}
                            imageUrl={item.imageUrl}
                            category={item.category}
                        />
                    ))}
                </div>
            </div>
        );
    }
}

export default Categories;
