import React, { useContext, useState } from 'react';
import { CartContext } from '../../components/Context';
import Header from '../../components/header/Header';
import Footer from '../../components/footer/Footer';
import { Link } from 'react-router-dom';
import { useParams } from 'react-router-dom';
import './tovar.css';


const Tovar = () => {
  const { items } = useContext(CartContext); // Получение данных из контекста
  const { id } = useParams(); // Получение ID товара
  const item = items.find((item) => item.id === id); // Поиск конкретного товара

  if (!item) return <p>Товар не найден</p>; // Обработка ошибки, если товара нет

  return (
    <>
      <h1>{item.title}</h1>
      <p>{item.price}</p>
      <p>{item.category}</p>
      <img src={item.imageUrl} alt="products" />
      {/* Остальной код */}
      <div className="container">
{/* Основной блок товара */}
<p className="product-title">{item.category}</p>
<h1 className="product-title2">{item.title}</h1>
<div className="preinfo">
    <p className="product-meta">Артикул: 21312132 | Гарантия: 1 год</p>
    <p className="product-rating">
        <img src="img/icons/stars.png" alt="stars" />
        <span className="rating-text">(8 отзывов)</span>
    </p>
</div>
<section className="product-section">
    <div className="thumbnail-gallery">
        <img src={item.imageUrl} alt="Гитара миниатюра 1" className="thumbnail" />
        <img
            src="https://www.muztorg.ru/files/dxp/j3t/yfl/88w/80g/gw8/8kk/4ko/s/dxpj3tyfl88w80ggw88kk4kos.jpg"
            alt="Гитара миниатюра 2"
            className="thumbnail"
        />
    </div>
    <div className="product-gallery">
        <div className="img-main">
            <img
                src="https://www.muztorg.ru/files/2jp/wx4/khs/2o0/8o4/4wc/ssk/s80/8/2jpwx4khs2o08o44wcssks808.jpg"
                alt={item.title}
                className="main-image"
            />
        </div>
    </div>
</section>
 
<Footer />
     </div>
    </>
  );
};

export default Tovar;
