import toCart from './../../img/products/ToCart.svg'
import cartIcon from './../../img/icons/cart.png'
import inCart from './../../img/pictures/toCart.svg'
import React from "react";

function Card(props) {
        console.log(props);
        const [isAdded, setIsAdded] = React.useState(false);

        const onAdd = () => {
            setIsAdded(true);
           
        }


    return(
        <div className="card">
            
                <button className="img">
                    <img src={props.imageUrl} alt='products' />
                </button>
                        <p>{props.category}</p>
                        <a>{props.title}</a>
                        <b>{props.price} руб.</b>
            
                    <button className="add"  >
                    <img onClick={onAdd} className="add" src={isAdded ? {toCart} : {inCart}} alt='inCart' />
                        <div className='textBtn'>
                         
                           
                            <img src={cartIcon} alt='addtocart'></img>
                            </div>
                    </button>
                
            </div>
    );
}

export default Card;
