import toCart from './../../img/pictures/toCart.svg'
import cartIcon from './../../img/icons/cart.png'
import inCart from './../../img/pictures/inCart.svg'
import React from "react";

function Card(props) {
        console.log(props);
        const [isAdded, setIsAdded] = React.useState(false);

        const onAdd = () => {
            setIsAdded(true);
           
        }


    return(
        <div className="card">
            
                <button className="img">
                    <img src={props.imageUrl} alt='products' />
                </button>
                        <p>{props.category}</p>
                        <a>{props.title}</a>
                        <b>{props.price} руб.</b>
            
                    <button className="img2"  onClick={onAdd} >
                    {toCart}
               
                        <img src={isAdded ? ( {toCart} ) : ({inCart} )}></img>
                            {/* В корзину */}
                            {/* <img src={cartIcon} alt='addtocart'></img> */}
                           
                    </button>
                
            </div>
    );
}

export default Card;
