import toCart from './../../img/pictures/toCart.svg'
import cartIcon from './../../img/icons/cart.png'
import inCart from './../../img/pictures/inCart.svg'
import React from "react";

function Card(props) {
        console.log(props);
        const [isAdded, setIsAdded] = React.useState(false);

        const onAdd = () => {
            setIsAdded(true);
           
        }

    return(
        <>
        
        
        <div className="card">
           
                <button className="img">
                    <img src={props.imageUrl} alt='products' />
                </button>
                        <p>{props.category}</p>
                        <a>{props.title}</a>
                        <b>{props.price} руб.</b>
            
                    <button className='add' onClick={onAdd} >
                        {
  !isAdded ? (
    <Navigate to="/cart">
      <img className="img2" src={toCart} alt="Add to Cart" />
    </Navigate>
  ) : (
    <img className="img2" src={inCart} alt="In Cart" />
  )
}
                        <img className="img2"  src={isAdded ? inCart : toCart} ></img>
                            {/* В корзину */}
                            {/* <img src={cartIcon} alt='addtocart'></img> */}  
                           
                    </button>
                
            </div>
            
            </>
    );
}

export default Card;
