import React, {useState, createContext} from "react";
import axios from "axios";
import { useEffect, useCallback  } from 'react';
export const CartContext = createContext();

const Context = (props) => {

  const [items, setitems] = React.useState([]);
    const [cartItems, setCartItems] = React.useState([]);
    
    // Преобразуем price в число
    const processedItems = cartItems.map((item) => ({
      ...item,
      price: Number(item.price), // Убедимся, что price числовой
  }));


    // Загрузка товаров в корзине из API
    useEffect(() => {
        const fetchCartItems = async () => {
            try {
                const { data } = await axios.get('https://673876654eb22e24fca800c5.mockapi.io/cart');
                setCartItems(data);
            } catch (error) {
                console.error("Ошибка при загрузке корзины:", error);
            }
        };
        fetchCartItems();
    }, []);

  const onAddToCart = useCallback((item) => {
    setCartItems((prevItems) => {
        if (prevItems.some((cartItem) => cartItem.title === item.title)) {
            return prevItems; // Если товар уже в корзине, не добавляем его снова
        }
        return [...prevItems, item];
    });
}, []);

// Проверка, добавлен ли товар в корзину
const isItemInCart = (title) => {
    return cartItems.some((cartItem) => cartItem.title === title);
};
  
    const onRemoveItem = (id) => {
      
      axios.delete(`https://673876654eb22e24fca800c5.mockapi.io/cart/${id}`);
      setCartItems((prev) => prev.filter(item => item.id !== id));
    }

    const [activeCategory, setActiveCategory] = useState(''); // Состояние для активной категории

  const value = {
    items,
    isItemInCart,
    processedItems,
    setitems,
    cartItems,
    activeCategory,
    setActiveCategory,
    setCartItems,
    onAddToCart,
    onRemoveItem,
  }
    return (
      <CartContext.Provider value={value}>
        {props.children}
      </CartContext.Provider>
        
        
    );
}
export default Context;
