import React, { useContext, useState } from 'react';
import Card from '../card/Card';
import { CartContext } from '../Context';
import './product.css';
import axios from 'axios';

const Products = () => {
    const { items, setitems } = useContext(CartContext);
    const { cartItems, onAddToCart, setCartItems } = useContext(CartContext);
    const [activeCategory, setActiveCategory] = useState(''); // Состояние для активной категории

    // Загрузка данных
    React.useEffect(() => {
        axios.get('https://673876654eb22e24fca800c5.mockapi.io/items').then((res) => {
            setitems(res.data);
        });
        axios.get('https://673876654eb22e24fca800c5.mockapi.io/cart').then((res) => {
            setCartItems(res.data);
        });
    }, []);

    // Обработчик выбора категории
    const handleCategoryClick = (category) => {
        setActiveCategory(category);
    };

    // Фильтрация карточек
    const filteredItems = activeCategory
        ? items.filter((item) => item.category === activeCategory)
        : items;

    return (
        <section className="Products">
            <div className="container">
                
            
                {/* Категории */}
                <span>Категории</span>
                <div className="categories">
                  
                    <button
                        onClick={() => handleCategoryClick('')}
                        className={activeCategory === '' ? 'active' : ''}
                    >
                        Все
                    </button>
                    <button
                        onClick={() => handleCategoryClick('Акустическая гитара')}
                        className={activeCategory === 'Акустические гитары' ? 'active' : ''}
                    >
                        Акустические гитары
                    </button>
                    <button
                        onClick={() => handleCategoryClick('Электрогитара')}
                        className={activeCategory === 'Электрогитары' ? 'active' : ''}
                    >
                        Электрогитары
                    </button>
                    <button
                        onClick={() => handleCategoryClick('Классическая гитара')}
                        className={activeCategory === 'Классические гитары' ? 'active' : ''}
                    >
                        Классические гитары
                    </button>
                    <button
                        onClick={() => handleCategoryClick('Усилитель для гитары')}
                        className={activeCategory === 'Усилители для гитар' ? 'active' : ''}
                    >
                        Усилители для гитар
                    </button>
                    <button
                        onClick={() => handleCategoryClick('Цифровое пианино')}
                        className={activeCategory === 'Цифровое пианино' ? 'active' : ''}
                    >
                        Цифровое пианино
                    </button>
                    <button
                        onClick={() => handleCategoryClick('Барабаны')}
                        className={activeCategory === 'Барабаны' ? 'active' : ''}
                    >
                        Барабаны
                    </button>
                </div>
                <span>Популярные товары</span>
                {/* Карточки товаров */}
                <div className="tovary">
                    {filteredItems.map((item) => (
                        <Card
                            key={item.id}
                            onClickAdd={(obj) => onAddToCart(obj)}
                            {...item}
                        />
                    ))}
                </div>
            </div>
        </section>
    );
};

export default Products;
