import React, { useContext } from "react";
import { CartContext } from '../../components/Context'; 
import toCart from './../../img/pictures/toCart.svg';
import inCart from './../../img/pictures/inCart.svg';

function Card({ id, imageUrl, category, title, price, onClickAdd }) {
    const { cartItems } = useContext(CartContext);

    // Проверка, находится ли товар в корзине
    const isAdded = cartItems.some((cartItem) => cartItem.id === id);

    // Преобразование цены
    const numericPrice = Number(price);
    const formattedPrice = numericPrice.toLocaleString('ru-RU');

    return (
        <div className="card" key={id}>
            <img src={imageUrl} alt="products" />
            <p>{category}</p>
            <a>{title}</a>
            <b>{formattedPrice} ₽.</b>
            <button className="add">
                {!isAdded ? (
                    <button onClick={() => onClickAdd({ id, title, price, category, imageUrl })}>
                        <img className="img2" src={toCart} alt="Add to Cart" />
                    </button>
                ) : (
                    <a href="/cart">
                        <img className="img2" src={inCart} alt="In Cart" />
                    </a>
                )}
            </button>
        </div>
    );
}

export default Card;
