import './tovar.css'
import Header from '../../components/header/Header';
import Footer from '../../components/footer/Footer';
import React from 'react';
import { Link } from 'react-router-dom';
import toCart from './../../img/pictures/toCart.svg'
import inCart from './../../img/pictures/inCart.svg'
import { useParams } from 'react-router-dom';
import axios from "axios";

const Tovar = ({ onClickAdd }) => {
  const { id } = useParams(); // Извлечение id из URL
  const [product, setProduct] = React.useState(null); // Состояние для хранения данных товара
  const [isLoading, setIsLoading] = React.useState(true);

  React.useEffect(() => {
      // Загрузка данных о товаре с помощью id
      axios
          .get(`https://673876654eb22e24fca800c5.mockapi.io/items/${id}`)
          .then((res) => {
              setProduct(res.data);
              setIsLoading(false);
          })
          .catch((err) => {
              console.error('Ошибка загрузки данных товара:', err);
              setIsLoading(false);
          });
  }, [id]);

  if (isLoading) {
      return <p>Загрузка...</p>;
  }

  if (!product) {
      return <p>Товар не найден.</p>;
  }
    return (
        <>
        <Header />
        <div className="container">
    {/* <!-- Основной блок товара --> */}
    
    
  </div>
  <Footer />
        </>
    );
}

export default Tovar;
