import React, {useState, createContext} from "react";
import axios from "axios";
import { useEffect, useCallback  } from 'react';
export const CartContext = createContext();

const Context = (props) => {
    const [cartItems, setCartItems] = React.useState([]);
    
    const onAddToCart = useCallback(async (obj) => {
      try {
          await  axios.post("https://673876654eb22e24fca800c5.mockapi.io/items", obj).then(res =>setCartItems(prev => [...prev, res.data])) 
          setCartItems((prev) => [...prev, obj]);
      } catch (error) {
          console.error("Ошибка при добавлении товара:", error);
      }
  }, []);

  // Загрузка данных корзины при монтировании компонента
  useEffect(() => {
      const fetchData = async () => {
          try {
              const response = await axios.get('https://673876654eb22e24fca800c5.mockapi.io/cart');
              setCartItems(response.data);
          } catch (error) {
              console.error("Ошибка при загрузке данных:", error);
          }
      };

      fetchData();
  }, []); // Выполняется только при монтировании компонента
  
    const onRemoveItem = (id) => {
      
      axios.delete(`https://673876654eb22e24fca800c5.mockapi.io/cart/${id}`);
      setCartItems((prev) => prev.filter(item => item.id !== id));
    }

  const value = {
    cartItems,
    setCartItems,
    onAddToCart,
    onRemoveItem,
  }
    return (
      <CartContext.Provider value={value}>
        {props.children}
      </CartContext.Provider>
        
        
    );
}
export default Context;
