import React, { useContext } from "react";
import { Link } from 'react-router-dom';
import { CartContext } from '../../components/Context'; // Импортируем контекст
import toCart from './../../img/pictures/toCart.svg';
import inCart from './../../img/pictures/inCart.svg';

function Card({ id, imageUrl, category, title, price }) {
    const { cartItems, onAddToCart } = useContext(CartContext); // Получаем из контекста данные о корзине и функцию для добавления товара

    // Проверяем, есть ли товар в корзине
    const isAdded = cartItems.some((cartItem) => cartItem.id === id);

    // Преобразуем price в число и форматируем
    const numericPrice = Number(price);
    const formattedPrice = numericPrice.toLocaleString('ru-RU'); // Добавляем пробелы в разряды

    // Обработчик добавления товара в корзину
    const handleAddToCart = (item) => {
        console.log('Item before adding to cart:', item); // Логируем item перед добавлением
        const productToAdd = {
            id: item.id, // Убедитесь, что id правильное
            title: item.title,
            price: item.price,
            category: item.category,
            imageUrl: item.imageUrl,
        };
        onAddToCart(productToAdd);
    };

    return (
        <div className="card" key={id}>
            <Link to={`/tovar/${encodeURIComponent(title)}`}>
                <button className="img">
                    <img src={imageUrl} alt="products" />
                </button>
            </Link>
            <p>{category}</p>
            <a>{title}</a>
            <b>{formattedPrice} ₽.</b> {/* Отображаем форматированную цену */}
            <button className="add">
                {!isAdded ? (
                    <button className="" onClick={handleAddToCart} disabled={isAdded}>
                        <img className="img2" src={toCart} alt="Add to Cart" />
                    </button>
                ) : (
                    <Link to="/cart">
                        <img className="img2" src={inCart} alt="In Cart" />
                    </Link>
                )}
            </button>
        </div>
    );
}

export default Card;
