import React, { useState, createContext, useEffect, useCallback } from "react";
import axios from "axios";

export const CartContext = createContext();

const Context = (props) => {
    const [items, setitems] = useState([]); // Состояние для всех товаров
    const [cartItems, setCartItems] = useState([]); // Состояние для корзины
    const [activeCategory, setActiveCategory] = useState(''); // Состояние для активной категории

    // Загружаем товары
    useEffect(() => {
        const fetchItems = async () => {
            try {
                const response = await axios.get('https://example.com/api/items');
                setitems(response.data);
            } catch (error) {
                console.error("Ошибка при загрузке товаров:", error);
            }
        };
        fetchItems();
    }, []);

    // Загружаем корзину
    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get('https://673876654eb22e24fca800c5.mockapi.io/cart');
                setCartItems(response.data);
            } catch (error) {
                console.error("Ошибка при загрузке данных корзины:", error);
            }
        };
        fetchData();
    }, []);

    // Добавляем товар в корзину
    const onAddToCart = useCallback(async (obj) => {
        try {
            const { data } = await axios.post(
                'https://673876654eb22e24fca800c5.mockapi.io/cart',
                obj
            );
            setCartItems((prev) => [...prev, data]);
        } catch (error) {
            console.error("Ошибка при добавлении товара:", error);
        }
    }, []);

    // Удаляем товар из корзины
    const onRemoveItem = async (id) => {
        try {
            await axios.delete(`https://673876654eb22e24fca800c5.mockapi.io/cart/${id}`);
            setCartItems((prev) => prev.filter(item => item.id !== id));
        } catch (error) {
            console.error("Ошибка при удалении товара:", error);
        }
    };

    // Фильтрация товаров по категории
    const filteredItems = activeCategory
        ? items.filter(item => item.category === activeCategory)
        : items;

    const value = {
        items: filteredItems, // Передаем отфильтрованные товары
        setitems,
        cartItems,
        activeCategory,
        setActiveCategory,
        setCartItems,
        onAddToCart,
        onRemoveItem,
    };

    return (
        <CartContext.Provider value={value}>
            {props.children}
        </CartContext.Provider>
    );
};

export default Context;
