import React, { useState, useEffect } from 'react';
import Card from '../card/Card';
import './categories.css';
import axios from 'axios';

const Categories = () => {
    // Состояния
    const [categories] = useState([
        { key: 'Акустические гитары', name: 'Акустические гитара' },
        { key: 'Электрогитары', name: 'Электрогитары' },
        { key: 'Классические гитары', name: 'Классические гитары' },
        { key: 'Усилители для гитар', name: 'Усилители для гитар' },
        { key: 'Цифровое пианино', name: 'Цифровое пианино' },
        { key: 'Барабаны', name: 'Барабаны' },
    ]);

    const [activeCategory, setActiveCategory] = useState('');
    const [items, setItems] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // Загрузка данных из MockAPI
    useEffect(() => {
        axios
            .get('https://673876654eb22e24fca800c5.mockapi.io/items')
            .then((response) => {
                setItems(response.data);
                setLoading(false);
            })
            .catch((error) => {
                setError(error.message);
                setLoading(false);
            });
    }, []);

    // Обработчик выбора категории
    const handleCategoryClick = (categoryKey) => {
        setActiveCategory(categoryKey);
    };

    // Фильтрация карточек по категории
    const filteredItems = activeCategory
        ? items.filter((item) => item.category === activeCategory)
        : items;

    return (
        <div className="container">
            {/* Блок категорий */}
            <div className="categories">
                <span>Категории</span>
                <div className="classes">
                    {categories.map((category) => (
                        <button
                            key={category.key}
                            onClick={() => handleCategoryClick(category.key)}
                            className={activeCategory === category.key ? 'active' : ''}
                        >
                            {category.name}
                        </button>
                    ))}
                </div>
            </div>

            {/* Состояние загрузки/ошибки */}
            {loading && <p>Загрузка данных...</p>}
            {error && <p style={{ color: 'red' }}>{error}</p>}

            {/* Блок карточек */}
            <div className="cards">
                {!loading && filteredItems.length === 0 && (
                    <p>Нет элементов в этой категории.</p>
                )}
                {filteredItems.map((item) => (
                    <Card
                        key={item.title} // Используем уникальное значение
                        title={item.title}
                        price={item.price}
                        imageUrl={item.imageUrl}
                        category={item.category}
                    />
                ))}
            </div>
        </div>
    );
};

export default Categories;
