import React from "react";
import "./login.css";
import { Navigate } from "react-router-dom";
import { Link } from 'react-router-dom'
import { useDispatch, useSelector } from 'react-redux'
import Header from '../../components/header/Header'
import { useForm } from 'react-hook-form'
import { fetchAuth, selectIsAuth } from "../../redux/slices/auth";


export const Login = () => {
  
  const isAuth = useSelector(selectIsAuth);
  const dispatch = useDispatch();
  const { 
    register,
    handleSubmit,
    formState: { errors, isValid},
    } = useForm({
    defaultValues: {
      email: '',
      password: ''
    },
    mode: 'onChange',
  });



  const onSubmit = async (values) => {
    const data = await dispatch(fetchAuth(values));

    if (!data.payload) {
    return alert ('Неверный логин или пароль')
    }

    if ('token' in data.payload) {
      window.localStorage.setItem('token', data.payload.token);
    }

  };

  
  
  if (isAuth) {
    return <Navigate to="/" />
  };
 

  return (
    <>
    <Header />
    <section className="Login">
      
        <div className="preview">
        
        <h3 className="text-center">Вход</h3>
        <div className="registration-cssave" >
    <form onSubmit={handleSubmit(onSubmit)}>
     
        <div className="form-group">
          <input className="form-control-item" type="email" name="email" id="email" placeholder="Email" required
          {...register("email", { 
            required: "Введите почту",
            pattern: {
              value: /^[^\s@]+@[^\s@]+\.[^\s@]+$/,
              message: "Введите корректный email",
            },
          })} />
        </div> 
        <div className="form-group">
            <input className="form-control-item" type="password" name="Пароль" minLength="3" id="password" placeholder="Пароль" required
            error={Boolean(errors.password?.message)}
            helperText={errors.password?.message} 
            {...register('password', { required: 'Введите пароль'})} />
            
        </div>
        <div className="form-group">
          
            <button className="btn btn-primary btn-block create-account" type="submit">ВОЙТИ</button>
        </div>
        <div className="form-group">
        <Link to='/register'> 
            <button className="register" type="submit">Нет аккаунта? <a>Зарегистрироваться</a></button>
            </Link>
        </div>
    </form>
</div>
        </div>
      
    </section>
    </>
  );
};

export default Login
