import React, { useContext, useState } from 'react';
import './admin.css';
import Header from './../../components/header/Header';
import Footer from '../../components/footer/Footer';
import { CartContext } from '../../components/Context';
import { useForm } from 'react-hook-form';

const Admin = () => {
  const { items, addProduct, updateProduct, deleteProduct } = useContext(CartContext);

  const isAdmin = () => {
    const token = localStorage.getItem("token");
    if (!token) {
      return false;
    }
    
    try {
      const decoded = jwtDecode(token);
      return decoded.role === "admin";
    } catch (e) {
      console.error("Invalid token", e);
      return false;
    }
  };
  

  const checkAdminRights = async () => {
    const token = localStorage.getItem("token");
    if (!token) {
      return false;
    }
  
    try {
      const response = await fetch("/api/check-role", {
        method: "GET",
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
  
      const data = await response.json();
      return data.role === "admin"; // Предполагается, что сервер возвращает роль пользователя
    } catch (error) {
      console.error("Ошибка проверки прав:", error);
      return false;
    }
  };
  

  // Добавление продукта
  const {
    register: addRegister,
    handleSubmit: handleAddSubmit,
    formState: { errors: addErrors },
    reset: resetAddForm,
  } = useForm({
    defaultValues: { category: '', title: '', price: 0, imageUrl: '' },
    mode: 'onChange',
  });

  // Обновление продукта
  const {
    register: updateRegister,
    handleSubmit: handleUpdateSubmit,
    formState: { errors: updateErrors },
    reset: resetUpdateForm,
  } = useForm({
    defaultValues: { id: '', category: '', title: '', price: 0, imageUrl: '' },
    mode: 'onChange',
  });

  // Удаление продукта
  const [deleteId, setDeleteId] = useState('');
  const [deleteError, setDeleteError] = useState('');
  const [showErrors, setShowErrors] = useState(false);

  // Добавление продукта
  const handleAdd = async (data) => {
    try {
      const productWithId = { ...data, id: Date.now() };
      await addProduct(productWithId);
      resetAddForm(); // Сброс формы после добавления
    } catch (e) {
      console.error(e);
    }
  };

  // Обновление продукта
  const handleUpdate = async (data) => {
    if (!data.id) {
      setShowErrors(true);
      return;
    }

    try {
      await updateProduct(data.id, {
        title: data.title,
        category: data.category,
        price: data.price,
        imageUrl: data.imageUrl,
      });
      resetUpdateForm(); // Сброс формы после обновления
      setShowErrors(false); // Скрыть ошибки после успешного обновления
    } catch (e) {
      console.error(e);
    }
  };

  // Удаление продукта
  const handleDelete = async () => {
    if (!deleteId) {
      setDeleteError('Введите ID продукта для удаления.');
      return;
    }

    try {
      await deleteProduct(deleteId);
      setDeleteError(''); // Сброс ошибок после успешного удаления
    } catch (e) {
      setDeleteError('Ошибка при удалении продукта. Проверьте ID.');
    }
  };

  return (
    <>
      <Header />
      <section className="admin">
        <div className="container">
          <h1 className="prei">Админ-панель</h1>

          {/* Добавление товара */}
          <div className="crud-section">
            <h2>Добавить продукт</h2>
            <form onSubmit={handleAddSubmit(handleAdd)}>
             
                <input
                  type="text"
                  placeholder="Название"
                  {...addRegister('title', { required: 'Введите название продукта.' })}
                  className="form-control-item"
                />
                {addErrors.title && <p className="check">{addErrors.title.message}</p>}
            
             
                <input
                  type="text"
                  placeholder="Категория"
                  {...addRegister('category', { required: 'Введите категорию продукта.' })}
                  className="form-control-item"
                />
                {addErrors.category && <p className="check">{addErrors.category.message}</p>}
            
           
                <input
                  type="number"
                  placeholder="Цена"
                  {...addRegister('price', {
                    required: 'Введите цену продукта.',
                    min: { value: 1, message: 'Цена должна быть больше 0.' },
                  })}
                  className="form-control-item"
                />
                {addErrors.price && <p className="check">{addErrors.price.message}</p>}
            
            
                <input
                  type="text"
                  placeholder="URL изображения"
                  {...addRegister('imageUrl', { required: 'Введите URL изображения продукта.' })}
                  className="form-control-item"
                />
                {addErrors.imageUrl && <p className="check">{addErrors.imageUrl.message}</p>}
             
              <button type="submit" className="CRUD-btn">
                Добавить продукт
              </button>
            </form>
          </div>

          {/* Обновление товара */}
          <div className="crud-section">
            <h2>Обновить продукт</h2>
            <form onSubmit={handleUpdateSubmit(handleUpdate)}>
              
                <input
                  type="text"
                  placeholder="ID продукта"
                  {...updateRegister('id', { required: 'Введите ID продукта для обновления.' })}
                  className="form-control-item"
                />
                {updateErrors.id && <p className="check">{updateErrors.id.message}</p>}
             
             
                <input
                  type="text"
                  placeholder="Новое название"
                  {...updateRegister('title', { required: 'Введите новое название.' })}
                  className="form-control-item"
                />
                {updateErrors.title && <p className="check">{updateErrors.title.message}</p>}
              
             
                <input
                  type="text"
                  placeholder="Категория"
                  {...updateRegister('category')}
                  className="form-control-item"
                />
            
             
                <input
                  type="number"
                  placeholder="Новая цена"
                  {...updateRegister('price', {
                    min: { value: 1, message: 'Цена должна быть больше 0.' },
                  })}
                  className="form-control-item"
                />
                {updateErrors.price && <p className="check">{updateErrors.price.message}</p>}
             
              
                <input
                  type="text"
                  placeholder="URL изображения"
                  {...updateRegister('imageUrl')}
                  className="form-control-item"
                />
             
              <button type="submit" className="CRUD-btn">
                Обновить продукт
              </button>
            </form>
          </div>

          {/* Удаление товара */}
          <div className="crud-section">
            <h2>Удалить продукт</h2>
            <input
              type="number"
              placeholder="ID продукта для удаления"
              value={deleteId}
              onChange={(e) => setDeleteId(e.target.value)}
              className="form-control-item"
            />
            {deleteError && <p className="check">{deleteError}</p>}
            <button className="CRUD-btn" onClick={handleDelete}>
              Удалить продукт
            </button>
          </div>

          {/* Список товаров */}
          <div className="crud-section">
            <h2>Список товаров</h2>
            {items.length > 0 ? (
              <table className="product-table">
                <thead>
                  <tr className="shapka">
                    <th>ID</th>
                    <th>Название</th>
                    <th>Категория</th>
                    <th>Цена</th>
                    <th>Изображение</th>
                  </tr>
                </thead>
                <tbody>
                  {items.map((item) => (
                    <tr key={item.id}>
                      <td>{item.id}</td>
                      <td>{item.title}</td>
                      <td>{item.category}</td>
                      <td>{item.price} руб.</td>
                      <td>
                        <img
                          src={item.imageUrl}
                          alt={item.title}
                          width="50"
                          height="50"
                        />
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            ) : (
              <p>Список товаров пуст</p>
            )}
          </div>
        </div>
      </section>
      <Footer />
    </>
  );
};

export default Admin;
