import React, { useContext, useState } from 'react';
import { CartContext } from '../../components/Context';
import Header from '../../components/header/Header';
import Footer from '../../components/footer/Footer';
import { Link } from 'react-router-dom';
import './tovar.css';
import { useParams } from 'react-router-dom';

const Tovar = () => {
  const { items } = useContext(CartContext); // Получение данных из контекста
  const { id } = useParams(); // Получение ID товара
  const item = items.find((item) => item.id === id); // Поиск конкретного товара

  if (!item) return <p>Товар не найден</p>; // Обработка ошибки, если товара нет

  return (
    <>
      <h1>{item.title}</h1>
      <p>{item.price}</p>
      {/* Остальной код */}
      <Footer />
    </>
  );
};

export default Tovar;
