import React, { useState } from "react";
import "./registration.css";
import { Link } from 'react-router-dom'
import Header from '../../components/header/Header'
import { fetchRegister, selectIsAuth } from "../../redux/slices/auth";
import { useDispatch, useSelector } from 'react-redux'
import { Navigate } from "react-router-dom";
import { useForm } from 'react-hook-form'


export const Registration = () => {
    const isAuth = useSelector(selectIsAuth);
    const dispatch = useDispatch();
    const { 
      register,
      handleSubmit,
      formState: { errors, isValid},
      } = useForm({
      defaultValues: {
        fullName: '',
        email: '',
        password: '',
      },
      mode: 'onChange',
    });

    const onSubmit = async (values) => {
        const data = await dispatch(fetchRegister(values));
    
        if (!data.payload) {
        return alert ('Не удалось зарегистрироваться')
        }
    
        if ('token' in data.payload) {
          window.localStorage.setItem('token', data.payload.token);
        }
    
      };
    
      
      
      if (isAuth) {
        return <Navigate to="/" />
      };

  return (
    <>
    <Header />
    <section className="Login">
      
        <div className="preview">
            
        
        <h3 className="text-center">Регистрация</h3>
        <div className="registration-cssave" >

        <form onSubmit={handleSubmit(onSubmit)}>
        <div class="form-group">
        {errors.fullName && <p className="error-text">123123{errors.fullName.message}</p>}
            <input className="form-control-item" type="text" name="username" maxlength="15" minlength="4" pattern="^[a-zA-Z0-9_.-]*$" id="username" placeholder="Логин" required 
          
            {...register('fullName', { required: 'Введите логин'})} />
            
        </div>
        <div class="form-group">
          <input className="form-control-item" type="email" name="email" id="email" placeholder="Email" required 
          
          error={Boolean(errors.email?.message)}
          helperText={errors.email?.message} 
          {...register('email', { required: 'Введите почту'})} />
            
        </div>
        <div class="form-group">
            <input className="form-control-item" type="password" name="Пароль" minlength="6" id="password" placeholder="Пароль" required 
            error={Boolean(errors.password?.message)}
            helperText={errors.password?.message} 
            {...register('password', { required: 'Введите пароль'})} />
        </div>
        <div class="form-group">
            <button disabled={!isValid} className="btn btn-primary btn-block create-account" type="submit"><a>СОЗДАТЬ</a></button>
        </div>
        <div class="form-group">
        <Link to='/login'> 
            <button className="log-in" type="submit">Есть аккаунт? <a>Войти</a></button>
            </Link>
        </div>
    </form>
</div>
        </div>
      
    </section>
    </>
  );
};

export default Registration
