import './catalog.css'
import Header from '../../components/header/Header'
import ExHeader from '../../components/ExHeader/ExHeader'
import Products from '../../components/Product/Product'
import Footer from './../../components/footer/Footer'
import Categories from '../../components/categories/Categories'
import React, { useState, useEffect } from 'react';
import Card from '../../components/card/Card'

const Catalog = ({ allItems }) => {
    const [currentPageItems, setCurrentPageItems] = useState([]);
    const [filteredItems, setFilteredItems] = useState([]);

    useEffect(() => {
        // Пример: Задаем товары текущей страницы (пагинация)
        const itemsForCurrentPage = allItems.slice(0, 10); // Только первые 10 товаров
        setCurrentPageItems(itemsForCurrentPage);
        setFilteredItems(itemsForCurrentPage); // Изначально фильтр совпадает с текущими товарами
      }, [allItems]);

    return (
        <>
        <Header />
        {/* <ExHeader /> */}
       
        <Categories
        visibleItems={currentPageItems}
        setFilteredItems={setFilteredItems}
      />
          <div className="products-grid">
        {filteredItems.map((item) => (
          <Card key={item.id} {...item} />
        ))}
      </div>
        <Products />
      
        <Footer />
        </>
    );
}

export default Catalog;
